    const productSelectors = document.querySelectorAll('.product-select');
    const modeSelectors = document.querySelectorAll('input[name="product"], #protocolSelect, #paymentSelect, #dosageSelect');
    const protocolSelector = document.getElementById('protocolSelect');
    const paymentSelector = document.getElementById('paymentSelect');
    const toggleBenefit = document.getElementById("toggleBenefit");
    const benefitBox = document.getElementById("benefitBox");
    const benefitText = document.getElementById("benefitText");
    const benefitLabels = document.getElementById("benefitLabels");

    productSelectors.forEach(element => {
        element.addEventListener('click', (event) => {
            const currentSelected = document.querySelectorAll('.product-select.selected');
            currentSelected.forEach(item => item.classList.remove('selected'));
            event.target.closest('.product-select').classList.add('selected');
            var productId = event.target.value;
            document.getElementById('product-feature').setAttribute('src','../assets/img_from_site/v2/product_'+productId+'.jpg');
            if ([1,4].includes(productId)) {
                document.querySelectorAll('.zofran').forEach((item) => {
                    item.style.display = 'none';
                });
            } else {
                document.querySelectorAll('.zofran').forEach((item) => {
                    item.style.display = 'block';
                });
            }
            updateDosageOptions(productId);
            updateProductToggles(productId);
            updatePriceDetails();
        });
    });

    modeSelectors.forEach(element => {
        element.addEventListener('change', (event) => {
            updatePriceDetails();
        });
    });

    paymentSelector.addEventListener('change', (event) => {
        updateProtocolSelectorText(event.target.value);
    });

    protocolSelector.addEventListener('change', (event) => {
        const upfrontOption = paymentSelector.querySelector('[value="upfront"]');
        upfrontOption.disabled = event.target.value == 'month-to-month';
        updateBenefitContent();
    });

    const updateProtocolSelectorText = function(payment) {
        const protocolSelectors = document.querySelectorAll('#protocolSelect option');
        const regex = /Billed Monthly /;
        protocolSelectors.forEach((selector) => {
            if (payment == 'upfront') {
                selector.innerHTML = selector.innerHTML.replace(regex, '');
            } else {
                if (selector.innerHTML.search(regex) == -1 && selector.innerHTML.search(/Month-to-Month/) == -1) {
                    selector.innerHTML = selector.innerHTML.replace(/ \(/, ' Billed Monthly (');
                }
            }
        });
    };

    const updateProductToggles = function(productId) {
        const productToggles = document.querySelectorAll('.product-toggle');
        productToggles.forEach(toggle => {
            toggle.style.display = 'none';
            if (toggle.classList.contains('product'+productId)) {
                toggle.style.display = 'block';
            }
        });
    };

    const updatePriceDetails = function() {
        const productId = document.querySelector('input[name="product"]:checked').value;
        const protocolLength = document.getElementById('protocolSelect');
        const paymentPlan = document.getElementById('paymentSelect').value;
        const dosage = document.getElementById('dosageSelect').value ?? 'default';
        const couponBox = document.getElementById('couponBox');
        const couponDiscountAmount = document.getElementById('couponDiscountAmount');

        var price = products[productId]['dosage'][dosage]['price'];
        var couponDiscount = products[productId]['dosage'][dosage]['coupon_discount'];
        var couponPrice = products[productId]['dosage'][dosage]['coupon_price'];
        var followupPrice = products[productId]['dosage'][dosage]['followup_price'];
        var couponType = couponDiscount > 0 ? (couponPrice == followupPrice ? 'lifetime' : 'one-time') : null;
        var protocolDiscount = Number(protocolLength.selectedOptions[0].dataset.discount);
        var protocolMonths = Number(protocolLength.selectedOptions[0].dataset.months);
        var priceToday = '';
        var priceFollowUp = '';

        if (couponDiscount > 0) {
            couponBox.style.display = 'inline-block';
            couponDiscountAmount.innerHTML = '-$'+String(couponDiscount);
        } else {
            couponBox.style.display = 'none';
            couponDiscountAmount.innerHTML = '--';
        }

        if (paymentPlan == 'upfront' && protocolLength.value != 'month-to-month') {
            const upfrontDiscount = .1;
            priceToday = (((price - protocolDiscount - (couponType == 'lifetime' ? couponDiscount : 0)) * protocolMonths) * (1 - upfrontDiscount)) - (couponType == 'one-time' ? couponDiscount : 0);
            priceFollowUp = ((followupPrice - protocolDiscount) * protocolMonths) * (1 - upfrontDiscount);
            document.getElementById('priceLine').innerHTML = '<b style="font-size:1.5rem;">$<span class="priceToday">'+priceToday.toFixed(2)+'</span></b>/today and then $<span class="priceFollowUp">'+priceFollowUp.toFixed(2)+' every '+protocolMonths+' months</span>';
        } else {
            priceToday = couponPrice - protocolDiscount;
            priceFollowUp = followupPrice - protocolDiscount;
            document.getElementById('priceLine').innerHTML = '<b style="font-size:1.5rem;">$<span class="priceToday">'+priceToday.toFixed(2)+'</span></b>/today for your first month '+(priceFollowUp > 0 ? 'then $<span class="priceFollowUp">'+priceFollowUp.toFixed(2)+'/mo</span> thereafter' : '');
        }
    };

    const updateDosageOptions = function(productId) {
        const dosageSelect = document.getElementById('dosageSelect');

        dosageSelect.innerHTML = '';

        const newOptions = products[productId]['dosage'] ?? [];
        const defaultPrice = newOptions['default'].coupon_price;
        for (let dosage in newOptions) {
            let optionData = newOptions[dosage];
            const newOption = document.createElement('option');
            newOption.value = dosage;
            newOption.dataset.additional_cost = optionData.coupon_price - (defaultPrice ?? 0);
            let standard = dosage == 'default' ? ' (Standard)' : '';
            let dosageText = dosage == 'default' ? optionData['dosage'] : dosage;
            newOption.textContent = dosageText+'/'+optionData['dosage_period']+standard+' (+$'+newOption.dataset.additional_cost+'/mo)';
            dosageSelect.appendChild(newOption);
        }
    };

    const updateBenefitContent = function() {
        const selectedOption = protocolSelector.options[protocolSelector.selectedIndex];
        benefitText.innerHTML = selectedOption.getAttribute("data-benefit");
        benefitLabels.innerHTML = "";
        const labels = selectedOption.getAttribute("data-labels").split(",");
        labels.forEach(label => {
            const [color, text] = label.split(":");
            const pill = document.createElement("span");
            pill.className = `benefit-pill ${color.trim()}`;
            pill.textContent = text.trim();
            benefitLabels.appendChild(pill);
        });
    }

    toggleBenefit.addEventListener("click", function () {
        if (!benefitBox.classList.contains("expanded")) {
            updateBenefitContent();
            benefitBox.classList.add("expanded");
            toggleBenefit.innerHTML = "Hide details <<";
        } else {
            benefitBox.classList.remove("expanded");
            toggleBenefit.innerHTML = "Why this is a great option >>";
        }
    });

    updatePriceDetails();
    updateProtocolSelectorText(paymentSelector.value);
</script>

